import React, { Component } from "react";
import example from "./photos/example.png";
import photo1 from "./photos/photo1.png";
import photo2 from "./photos/photo2.jpg";
import photo3 from "./photos/photo3.jpg";
import "./App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      location: "",
      file: "",
      imgUrl: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.readFile = this.readFile.bind(this);
  }

  handleChange(evt) {
    console.log(evt.target.value);
  }

  handleSubmit(e) {}

  readFile(e) {
    document.getElementById("output").style.display = "block";
    let reader;

    if (e.target.files[0]) {
      reader = new FileReader();

      reader.readAsDataURL(e.target.files[0]);
    }

    this.setState({
      file: URL.createObjectURL(e.target.files[0]),
      imgUrl: reader,
    });

    console.log(this.state.reader);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <section>
            <article className="textual">
              <h1>I Endorse BOOKER</h1>
              <p>
                Create your own endorsement graphic for Bernie and other
                progressive candidates to share over Instagram, Twitter,
                Facebook, emails, and so forth!
              </p>
            </article>
          </section>
        </header>

        <section className="body">
          <article className="textual">
            <div className="left-group">
              <form>
                {/* name */}
                <div className="input-group standard">
                  <input type="text" name="name" onChange={this.handleChange} placeholder="Jane Doe" />
                  <label htmlFor="name">Name</label>
                </div>

                {/* location */}
                <div className="input-group standard">
                  <input
                    type="text"
                    name="location"
                    onChange={this.handleChange}
                    placeholder="New York, New York"
                  />
                  <label htmlFor="location">Location</label>
                </div>

                {/* why */}
                <div className="input-group">
                  <textarea
                    onChange={this.handleChange}
                    name="blurb"
                    placeholder="Your text here"
                    rows="6"
                  />
                  <label htmlFor="blurb">Why do you endorse Charles Booker?</label>
                </div>

                {/* photo */}
                <div className="input-group upload-img">
                  <input
                    type="file"
                    id="inputImg"
                    accept="image/*"
                    onChange={this.readFile}
                  />
                  <label htmlFor="inputImg">Upload photo</label>
                  <img
                    id="output"
                    src={this.state.file}
                    alt=""
                    height="20"
                    width="20"
                  />
                  </div>

                <input type="image" src={photo1} id="pic" alt="" />
                <input type="image" src={photo2} id="pic" alt="" />
                <input type="image" src={photo3} id="pic" alt="" />



                <input
                  type="submit"
                  name="Generate!"
                  onSubmit={this.handleSubmit}
                />
              </form>
            </div>
            <div className="right-group">
              <canvas height="100" width="100" />
              <img className="example" name="example" src={example} alt="" />
            </div>
          </article>
        </section>
      </div>
    );
  }
}

export default App;
