import React, { Component } from "react";
import example from "./photos/example.png";
import {ReactComponent as TwitterIcon} from './photos/twitter.svg';
import {ReactComponent as FacebookIcon} from './photos/facebook.svg';
import {ReactComponent as InstagramIcon} from './photos/instagram.svg';
import photo1 from "./photos/photo1.png";
import photo2 from "./photos/photo2.jpg";
import photo3 from "./photos/photo3.jpg";
import "./App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      location: "",
      file: "",
      imgUrl: "",
      bg_pic: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.readFile = this.readFile.bind(this);
    this.setBgPic = this.setBgPic.bind(this);
  }

  handleChange(evt) {
     console.log(evt.target.name);
  }

  handleSubmit(e) {}

  setBgPic(e) {
    console.log(e.target.value);
  }

  readFile(e) {
    document.getElementById("output").style.display = "block";
    let reader;

    if (e.target.files[0]) {
      reader = new FileReader();

      reader.readAsDataURL(e.target.files[0]);
    }

    this.setState({
      file: URL.createObjectURL(e.target.files[0]),
      imgUrl: reader,
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <section>
            <article className="textual">
              <h1>I Endorse BOOKER</h1>
              <p>
                Create your own endorsement graphic for Bernie and other
                progressive candidates to share over Instagram, Twitter,
                Facebook, emails, and so forth!
              </p>
            </article>
          </section>
        </header>

        <section className="body">
          <article className="textual">
            <div className="left-group">
              <form>
                {/* name */}
                <div className="input-group standard">
                  <input type="text" name="name" onChange={this.handleChange} placeholder="Enter Full Name" />
                  <label htmlFor="name">Name</label>
                </div>

                {/* location */}
                <div className="input-group standard">
                  <input
                    type="text"
                    name="location"
                    onChange={this.handleChange}
                    placeholder="Enter Location"
                  />
                  <label htmlFor="location">Location</label>
                </div>

                {/* why */}
                <div className="input-group standard">
                  <textarea
                    onChange={this.handleChange}
                    name="blurb"
                    placeholder="Your text here"
                    rows="6"
                  />
                  <label htmlFor="blurb">
                    Why do you endorse Charles Booker?
                  </label>
                </div>

                {/* photo */}
                <div className="input-group non-text">
                  <input
                    type="file"
                    id="inputImg"
                    accept="image/*"
                    onChange={this.readFile}
                  />
                  <label htmlFor="inputImg">Upload photo</label>
                  <img
                    id="output"
                    src={this.state.file}
                    alt=""
                  />
                </div>

                <div>
            <input type="button" src={photo1} onClick={this.handleChange} />
           {/* <img src={photo1} name="photo1" onClick={this.handleChange} />
           <img src={photo2} name="photo2" onClick={this.handleChange} />
           <img src={photo3} name="photo3" onClick={this.handleChange} /> */}
           {/* <button class="btn"></button> */}
                </div>

                <input
                  type="submit"
                  name="Generate!"
                  onSubmit={this.handleSubmit}
                />
              </form>
            </div>
            <div className="right-group">
              <canvas height="100" width="100" />
              <img className="example" name="example" src={example} alt="" />

              {/* social */}
              <div class="social">
                <a href="https://twitter.com/Booker4KY" taget="_blank">
                  <TwitterIcon />
                </a>
                <a href="https://www.facebook.com/Booker4KY/" taget="_blank">
                  <FacebookIcon />
                </a>
                <a href="https://www.instagram.com/booker4ky" taget="_blank">
                  <InstagramIcon />
                </a>
                <p>
                If you like this, please follow me on <a href="https://twitter.com/Booker4KY" taget="_blank">Twitter</a> — and say hello! And when you share your image on social media, include the hashtag #IEndorseBooker.
              </p>
              </div>
            </div>
          </article>
        </section>
      </div>
    );
  }
}

export default App;
