import React, { Component } from "react";
import example from "./photos/example.png";
import {ReactComponent as TwitterIcon} from './photos/twitter.svg';
import {ReactComponent as FacebookIcon} from './photos/facebook.svg';
import {ReactComponent as InstagramIcon} from './photos/instagram.svg';
import photo1 from "./photos/photo1.png";
import photo2 from "./photos/photo2.jpg";
import photo3 from "./photos/photo3.jpg";
import "./App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      location: "",
      file: null,
      bgPhoto: "",
      bgColor: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.readFile = this.readFile.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    console.log(e.target.value);
    this.setState({
      ...this.state,
      [e.target.name]: e.target.value,
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    console.log(this.state);
  }

  readFile(e) {
    if (e.target.files[0]) {
      this.setState({
        ...this.state,
        file: URL.createObjectURL(e.target.files[0]),
      });
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <section>
            <article className="textual">
              <h1>I Endorse BOOKER</h1>
              <p>
                Create your own endorsement graphic for Bernie and other
                progressive candidates to share over Instagram, Twitter,
                Facebook, emails, and so forth!
              </p>
            </article>
          </section>
        </header>

        <section className="body">
          <article className="textual">
            <div className="left-group">
              <form onSubmit={this.handleSubmit}>
                {/* name */}
                <div className="input-group standard">
                  <input
                    type="text"
                    name="name"
                    onChange={this.handleChange}
                    placeholder="Enter Full Name"
                    value={this.state.name}
                  />
                  <label htmlFor="name">Name</label>
                </div>

                {/* location */}
                <div className="input-group standard">
                  <input
                    type="text"
                    name="location"
                    onChange={this.handleChange}
                    placeholder="Enter Location"
                    value={this.state.location}
                  />
                  <label htmlFor="location">Job Title/Location</label>
                </div>

                {/* why */}
                <div className="input-group standard">
                  <textarea
                    onChange={this.handleChange}
                    name="blurb"
                    placeholder="Your text here"
                    rows="6"
                    value={this.state.blurb}
                  />
                  <label htmlFor="blurb">
                    Why do you endorse Charles Booker?
                  </label>
                </div>

                {/* photo */}
                <div className="input-group non-text">
                  <input
                    type="file"
                    id="file"
                    accept="image/*"
                    onChange={this.readFile}
                  />
                  <label htmlFor="inputImg">Upload photo</label>
                  <img
                    id="output"
                    src={this.state.file}
                    alt=""
                  />
                </div>

                <input
                  type="radio"
                  name="bgColor"
                  id="redBG"
                  value="redBg"
                  onChange={this.handleChange}
                />
                <input
                  type="radio"
                  name="bgColor"
                  id="orangeBG"
                  value="orangeBg"
                  onChange={this.handleChange}
                />
                <input
                  type="radio"
                  name="bgColor"
                  id="yellowBG"
                  value="yellowBg"
                  onChange={this.handleChange}
                />

                <div>
                  <input
                    type="radio"
                    name="bgPhoto"
                    id="choice-1"
                    value="photo1"
                    onChange={this.handleChange}
                  />
                  <label for="choice-1">
                    <img src={photo1} name="photo1" alt="" />
                  </label>
                  <input
                    type="radio"
                    name="bgPhoto"
                    id="choice-2"
                    value="photo2"
                    onChange={this.handleChange}
                  />
                  <label for="choice-2">
                    <img src={photo2} name="photo1" alt="" />
                  </label>
                  <input
                    type="radio"
                    name="bgPhoto"
                    id="choice-3"
                    value="photo3"
                    onChange={this.handleChange}
                  />
                  <label for="choice-3">
                    <img src={photo3} name="photo3" alt="" />
                  </label>
                </div>

                <input type="submit" name="Generate!" />
              </form>
            </div>
            <div className="right-group">
              <canvas height="100" width="100" />
              <img className="example" name="example" src={example} alt="" />

              {/* social */}
              <div class="social">
                <a href="https://twitter.com/Booker4KY" taget="_blank">
                  <TwitterIcon />
                </a>
                <a href="https://www.facebook.com/Booker4KY/" taget="_blank">
                  <FacebookIcon />
                </a>
                <a href="https://www.instagram.com/booker4ky" taget="_blank">
                  <InstagramIcon />
                </a>
                <p>If you like this, please follow me on social media — and say hello! And when you share your image include the hashtag <bold>#IEndorseBooker</bold>.</p>
              </div>

            </div>
          </article>
        </section>
      </div>
    );
  }
}

export default App;
