import React, { Component } from "react";
import example from "./example.png";
import "./App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      location: "",
      imgUrl: "",
    };
  }

  handleChange(evt) {
    console.log(evt.target.value);
  }

  handleSubmit(input) {

  }

  readFile(input) {
  document.getElementById("avatar").style.display = "block";
  document.getElementById("avatar").style.visibility = "visible";

    if (input.files && input.files[0]) {
      var reader = new FileReader();

      reader.onload = function (e) {
    document.getElementById("avatar").src = e.target.result;

      };

   reader.readAsDataURL(input.files[0]);
      console.log("Img url..." + input.files[0])
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <section>
            <article className="textual">
              <h1>I Endorse BOOKER</h1>
              <p>
                Create your own endorsement graphic for Bernie and other
                progressive candidates to share over Instagram, Twitter,
                Facebook, emails, and so forth!
              </p>
            </article>
          </section>
        </header>

        <section className="body">
          <article className="textual">
            <div className="left-group">
              <form>
                <label>
                  Name:
                  <input type="text" name="name" onChange={this.handleChange} />
                </label>
                <label>
                  Location:
                  <input
                    type="text"
                    name="location"
                    onChange={this.handleChange}
                  />
                </label>
                <label>
                  Why do you endorse Charles Booker?
                  <textarea
                    onChange={this.handleChange}
                    name="blurb"
                    placeholder="Your text here"
                    rows="6"
                  />
                </label>
                <label>
                  Upload photo
                  <input type="file" id="avatar" onChange={this.readFile} />
                </label>
                <input
                  type="submit"
                  name="Generate!"
                  onSubmit={this.handleSubmit}
                />
              </form>
            </div>
            <div className="right-group">
              <canvas height="100" width="100" />
              <img class="example" src={example} alt="example" width="600" height="600" />
            </div>
          </article>
        </section>
      </div>
    );
  }
}

export default App;
